mapper.select:
SELECT COUNT(*)
FROM MEM
WHERE MEM_ID = #{mem_id}

SELECT MEM_NO, MEM_ID, MEM_NM, MEM_PW, LEVEL_NO, MEM_DEL 
FROM MEM
WHERE MEM_ID = #{mem_id}
AND MEM_PW = #{mem_pw}

mapper.update:
UPDATE MEM
SET MEM_PHONE = #{mem_phone}
	
	    ,MEM_GEN = #{mem_gen},
	    MEM_PW_GBN = #{mem_pw_gbn},
	    MEM_PWA = #{mem_pwa}
	
WHERE MEM_NO = #{mem_no}

UPDATE MEM
SET MEM_DEL = '0'
WHERE MEM_NO = #{mem_no}

UPDATE MEM
SET MEM_PW = #{mem_pw}
WHERE MEM_NO = #{mem_no}

mapper.select:
SELECT MEM_ID
FROM MEM
WHERE MEM_NM=#{mem_nm}
AND MEM_PHONE=#{mem_phone}
AND MEM_BD=#{mem_bd}
AND MEM_DEL=1

SELECT COUNT(*)
FROM MEM
WHERE MEM_NM=#{mem_nm}
AND MEM_PHONE=#{mem_phone}
AND MEM_BD=#{mem_bd}
AND MEM_DEL=1

SELECT COUNT(*)
FROM MEM
WHERE MEM_ID=#{mem_id}
AND  MEM_NM=#{mem_nm}
AND MEM_DEL=1

SELECT MEM_NO, MEM_ID, MEM_PW, MEM_NM, MEM_PHONE, MEM_GEN, 
	   MEM_BD, MEM_PW_GBN, MEM_PWA, LEVEL_NO
FROM MEM
WHERE MEM_ID=#{mem_id}
AND  MEM_NM=#{mem_nm}
AND  MEM_PW_GBN=#{mem_pw_gbn}
AND  MEM_PWA=#{mem_pwa}
AND MEM_DEL=1

SELECT COUNT(*)
FROM MEM
WHERE MEM_ID=#{mem_id}
AND  MEM_NM=#{mem_nm}
AND  MEM_PW_GBN=#{mem_pw_gbn}
AND  MEM_PWA=#{mem_pwa}
AND MEM_DEL=1

WITH ALLLIST AS( SELECT FREE_NO AS NO, '개인' AS TEAM_NM, 0 AS TBM_NO, 0 AS GBN, '자유게시판' AS TBM_NM, FREE_TITLE AS TITLE, FREE_CON AS CON, TO_CHAR(FREE_DT, 'YYYY-MM-DD') AS DT, FREE_HIT AS HIT, FREE_FILE AS B_FILE, MEM_NO, FREE_DEL AS DEL FROM FREE UNION ALL SELECT M.NO, NVL(M.TEAM_NM, '개인') AS TEAM_NM, NVL(TBM.TBM_NO, 0) AS TBM_NO, NVL(M.GBN, 1) AS GBN, NVL(TBM.TBM_NM, '체활모') AS TBM_NM, M.TITLE, M.CON, M.DT, M.HIT, B_FILE, M.MEM_NO, M.DEL FROM ( SELECT M.MATCH_NO AS NO, T.TEAM_NM AS TEAM_NM, M.TEAM_NO AS GBN, M.PLACE_NM AS TITLE, M.MATCH_CON AS CON, TO_CHAR(M.MATCH_DT, 'YYYY-MM-DD') AS DT, 0 AS HIT, M.MATCH_FILE AS B_FILE, M.MEM_NO, M.MATCH_DEL AS DEL FROM MATCHES M LEFT JOIN TEAM T ON M.TEAM_NO = T.TEAM_NO) M LEFT JOIN TB_MANAGE TBM ON M.GBN = TBM.TEAM_NO UNION ALL SELECT TB.TB_NO AS NO, T.TEAM_NM AS TEAM_NM, TB.TBM_NO AS TBM_NO, TM.TEAM_NO AS GBN, TM.TBM_NM AS TBM_NM, TB.TB_TITLE AS TITLE, TB_CON AS CON, TO_CHAR(TB.TB_DT, 'YYYY-MM-DD') AS DT, TB.TB_HIT AS HIT, TB.TB_FILE AS B_FILE, TB.MEM_NO, TB.TB_DEL AS DEL FROM T_BOARD TB, TB_MANAGE TM, TEAM T WHERE TB.TBM_NO = TM.TBM_NO AND TM.TEAM_NO = T.TEAM_NO AND TM.TBM_GBN NOT IN (3, 4) ) SELECT COUNT(*) FROM (SELECT NO, TEAM_NM, TBM_NO, GBN, TBM_NM, TITLE, CON, DT, HIT, B_FILE, MEM_NO, DEL, ROW_NUMBER() OVER (ORDER BY DT DESC) AS RNUM FROM ALLLIST WHERE DEL = 1 AND MEM_NO = #{mem_no}
AND TEAM_NM IN '개인'
AND TEAM_NM NOT IN '개인'
AND TITLE LIKE '%'|| #{searchTxt} ||'%'
AND TITLE LIKE '%'|| #{searchTxt} ||'%' OR CON LIKE '%'|| #{searchTxt} ||'%'
) AB

WITH ALLLIST AS( SELECT FREE_NO AS NO, '개인' AS TEAM_NM, 0 AS TBM_NO, 0 AS GBN, '자유게시판' AS TBM_NM, FREE_TITLE AS TITLE, FREE_CON AS CON, TO_CHAR(FREE_DT, 'YYYY-MM-DD') AS DT, FREE_HIT AS HIT, FREE_FILE AS B_FILE, MEM_NO, FREE_DEL AS DEL FROM FREE UNION ALL SELECT M.NO, NVL(M.TEAM_NM, '개인') AS TEAM_NM, NVL(TBM.TBM_NO, 0) AS TBM_NO, NVL(M.GBN, 1) AS GBN, NVL(TBM.TBM_NM, '체활모') AS TBM_NM, M.TITLE, M.CON, M.DT, M.HIT, B_FILE, M.MEM_NO, M.DEL FROM ( SELECT M.MATCH_NO AS NO, T.TEAM_NM AS TEAM_NM, M.TEAM_NO AS GBN, M.PLACE_NM AS TITLE, M.MATCH_CON AS CON, TO_CHAR(M.MATCH_DT, 'YYYY-MM-DD') AS DT, 0 AS HIT, M.MATCH_FILE AS B_FILE, M.MEM_NO, M.MATCH_DEL AS DEL FROM MATCHES M LEFT JOIN TEAM T ON M.TEAM_NO = T.TEAM_NO) M LEFT JOIN TB_MANAGE TBM ON M.GBN = TBM.TEAM_NO UNION ALL SELECT TB.TB_NO AS NO, T.TEAM_NM AS TEAM_NM, TB.TBM_NO AS TBM_NO, TM.TEAM_NO AS GBN, TM.TBM_NM AS TBM_NM, TB.TB_TITLE AS TITLE, TB_CON AS CON, TO_CHAR(TB.TB_DT, 'YYYY-MM-DD') AS DT, TB.TB_HIT AS HIT, TB.TB_FILE AS B_FILE, TB.MEM_NO, TB.TB_DEL AS DEL FROM T_BOARD TB, TB_MANAGE TM, TEAM T WHERE TB.TBM_NO = TM.TBM_NO AND TM.TEAM_NO = T.TEAM_NO AND TM.TBM_GBN NOT IN (3, 4) ) SELECT * FROM (SELECT NO, TEAM_NM, TBM_NO, GBN, TBM_NM, TITLE, CON, DT, HIT, B_FILE, MEM_NO, DEL, ROW_NUMBER() OVER (ORDER BY DT DESC) AS RNUM FROM ALLLIST WHERE DEL = 1 AND MEM_NO = #{mem_no}
AND TEAM_NM IN '개인'
AND TEAM_NM NOT IN '개인'
AND TITLE LIKE '%'|| #{searchTxt} ||'%'
AND TITLE LIKE '%'|| #{searchTxt} ||'%' OR CON LIKE '%'|| #{searchTxt} ||'%'
) AB WHERE AB.RNUM BETWEEN #{startCnt} AND #{endCnt}
