mapper.select:
SELECT COUNT(*) 
FROM CARD 
WHERE MEM_NO = #{mem_no}

SELECT SUBSTR(CARD_NUM1, 0, 4) || '-' || SUBSTR(CARD_NUM1, 5) AS CARD_NUM
FROM CARD
WHERE MEM_NO = #{mem_no}

SELECT MEM_NO, MEM_ID, MEM_NM, MEM_JOIN, MEM_PHONE FROM (SELECT MEM_NO, MEM_ID, MEM_NM, TO_CHAR(MEM_JOIN,'YYYY-MM-DD') AS MEM_JOIN, MEM_PHONE, ROW_NUMBER() OVER(ORDER BY MEM_NO DESC) AS RNUM FROM MEM WHERE MEM_NO != 4
AND MEM_ID LIKE '%' || #{searchTxt} || '%'
AND MEM_NM LIKE '%' || #{searchTxt} || '%'
) MEM WHERE MEM.RNUM BETWEEN #{startCnt} AND #{endCnt}
