mapper.select:
SELECT N.NOTICE_NO, N.NOTICE_TITLE, M.MEM_NM, TO_CHAR(N.NOTICE_DT, 'YYYY-MM-DD') AS NOTICE_DT, N.NOTICE_HIT, N.NOTICE_FILE, N.NOTICE_UP FROM NOTICE N, MEM M WHERE N.MEM_NO = M.MEM_NO AND NOTICE_DEL = 1 AND N.NOTICE_UP = 0 UNION ALL SELECT N.NOTICE_NO, N.NOTICE_TITLE, N.MEM_NM, N.NOTICE_DT, N.NOTICE_HIT, N.NOTICE_FILE, N.NOTICE_UP FROM(SELECT N.NOTICE_NO, N.NOTICE_TITLE, M.MEM_NM, TO_CHAR(N.NOTICE_DT, 'YYYY-MM-DD') AS NOTICE_DT, N.NOTICE_HIT, N.NOTICE_FILE, N.NOTICE_UP, ROW_NUMBER() OVER (ORDER BY NOTICE_NO DESC) AS RNUM FROM NOTICE N, MEM M WHERE N.MEM_NO = M.MEM_NO AND NOTICE_DEL = 1 AND N.NOTICE_UP = 1
AND N.NOTICE_TITLE LIKE '%' || #{searchTxt} || '%'
)N WHERE N.RNUM BETWEEN #{startCnt} AND #{endCnt}

mapper.update:
		UPDATE NOTICE SET NOTICE_TITLE = #{title},
		                NOTICE_CON = #{con},
		                NOTICE_FILE = #{NFile},
		                NOTICE_UP = #{upbtn}
                WHERE NOTICE_NO = #{no}
                AND MEM_NO = #{mno}
