mapper.select:
SELECT N.NOTICE_NO, N.NOTICE_TITLE, N.MEM_NM, N.NOTICE_DT, N.NOTICE_HIT, N.NOTICE_FILE FROM ( SELECT N.NOTICE_NO, N.NOTICE_TITLE, M.MEM_NM, TO_CHAR(N.NOTICE_DT, 'YYYY-MM-DD') AS NOTICE_DT, N.NOTICE_HIT, N.NOTICE_FILE, ROW_NUMBER() OVER(ORDER BY N.NOTICE_NO DESC) AS RNUM FROM NOTICE N INNER JOIN MEM M ON N.MEM_NO = M.MEM_NO WHERE NOTICE_DEL = 1
AND N.NOTICE_TITLE LIKE '%' || #{searchTxt} || '%'
)N WHERE N.RNUM BETWEEN #{startCnt} AND #{endCnt}

mapper.update:
		UPDATE NOTICE SET NOTICE_TITLE = #{title},
		                NOTICE_CON = #{con},
		                NOTICE_FILE = #{NFile}
                WHERE NOTICE_NO = #{no}
                AND MEM_NO = #{mno}
