mapper.select:
SELECT TB.TBM_NM, TB.TB_NO, TB.MEM_NM, TB.TB_TITLE, TB.TB_FILE, TB.TB_DT, TB.TB_HIT FROM (SELECT TBM.TBM_NM, TB.TB_NO, M.MEM_NM, TB.TB_TITLE, TB.TB_FILE, TO_CHAR(TB.TB_DT, 'YYYY-MM-DD') AS TB_DT, TB.TB_HIT, ROW_NUMBER()OVER(ORDER BY TB_NO DESC) AS RNUM FROM T_BOARD TB INNER JOIN TB_MANAGE TBM ON TB.TBM_NO = TBM.TBM_NO INNER JOIN MEM M ON TB.MEM_NO = M.MEM_NO WHERE TB_DEL = 1 AND TBM_NM = '자유게시판'
AND MEM_NM LIKE '%' || #{searchTxt} || '%'
AND TB_TITLE LIKE '%' || #{searchTxt} || '%'
) TB WHERE TB.RNUM BETWEEN #{startCnt} AND #{endCnt}

mapper.update:
		UPDATE T_BOARD SET TB_HIT = TB_HIT + 1
		WHERE TBM_NO = 1
        AND TB_NO = #{no}

UPDATE T_BOARD SET TB_DEL = 0
WHERE TBM_NO = 1
AND TB_NO = #{no}

		UPDATE T_BOARD SET TB_TITLE = #{title},
                    TB_CON = #{con},
                    TB_FILE = #{tbfile}
		WHERE TBM_NO = 1
		AND TB_NO = #{no}
