mapper.select:
SELECT T.TB_NO, M.MEM_NM, T.TB_TITLE, T.TB_CON, T.TB_FILE, TO_CHAR(T.TB_DT, 'YYYY-MM-DD') AS T_BOARD_DT, T.TB_HIT, T.TB_UP, T.TEAM_NO FROM T_BOARD T, MEM M WHERE T.MEM_NO = M.MEM_NO AND TB_DEL = 1 AND TB_UP = 0 AND TBM_NO = 2 AND TEAM_NO = #{tno} UNION ALL SELECT T.TB_NO, T.MEM_NM, T.TB_TITLE, T.TB_CON, T.TB_FILE, T.TB_DT, T.TB_HIT, T.TB_UP, T.TEAM_NO FROM ( SELECT T.TB_NO, M.MEM_NM, T.TB_TITLE, T.TB_CON, T.TB_FILE, TO_CHAR(T.TB_DT, 'YYYY-MM-DD') AS TB_DT, T.TB_HIT, T.TB_UP, T.TEAM_NO, ROW_NUMBER() OVER (ORDER BY TB_NO DESC) AS RNUM FROM T_BOARD T, MEM M WHERE T.MEM_NO = M.MEM_NO AND TB_DEL = 1 AND TB_UP = 1 AND TBM_NO = 2 AND TEAM_NO = #{tno}
AND T.TB_TITLE LIKE '%' || #{searchTxt} || '%'
) T WHERE T.RNUM BETWEEN #{startCnt} AND #{endCnt}

mapper.update:
UPDATE T_BOARD SET TB_HIT = TB_HIT + 1
WHERE TBM_NO = 2
AND TB_NO = #{no}

		UPDATE T_BOARD SET TB_TITLE = #{title},
		                TB_CON = #{con},
		                TB_FILE = #{NFile},
		                TB_UP = #{upbtn}
                WHERE TB_NO = #{no}
                AND MEM_NO = #{mno}

		UPDATE T_BOARD SET TB_DEL = 0
    	WHERE TBM_NO = 2
    	AND TB_NO = #{no}
    	AND TEAM_NO = #{tno}
